import React,{ useState } from 'react';


const Input=({onChange})=>{
    const [inputVal,setVal]=useState("");

    function change(e){
       setVal(e.target.value);
       onChange(e.target.value);
    }

    return(
    <React.Fragment>
        <label for="todo">
          Enter Todo
          <input name="todo" onChange={change} value={inputVal}/>
        </label>
    </React.Fragment>
    )

}


export default Input;
